import React,{useState} from 'react'

import Modal from 'react-modal'


Modal.setAppElement('#root')

const Notes = ({ removeHandler,notes, setNotes,title,text, date, newDate}) => {
    const [modalIsOpen, setModalIsOpen] = useState(false)
    // const [noteSave, setNoteSave] = useState({
    //     id: '',
    //     title: '',
    //     text: '',
    //     date: '',
    //     newDate:'',
    // })

    // const handleSubmit = (e) =>{
    //     e.preventDefault();
    //     notes.editNotes({...noteSave, newDate:moment().format("MMMM Do YYYY,h:mm:ss a")});
    //     setModalIsOpen(false);
    // }



    // const openNote = (note) =>{
    //     setNoteSave(note);
    // };

    return(
       <div className="notes">
            <li>
                <div className="date">{date}</div>
                <div className="date">{newDate}</div>
                <button onClick={removeHandler}className="remove-btn">DELETE</button> 
                <button onClick={()=> setModalIsOpen(true)}className="modal-btn">OPEN</button>
                <div>{title}</div>
                <div>{text}</div>
            </li>
           <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)}>
            <li className="note-item">
                <div className="date">{date}</div>
                <div>{title}</div>
                <div>{text}</div>
                <button onClick={()=> setModalIsOpen(false)} className="close-btn">Close</button>
            </li>
            </Modal>
       </div>     
    );
}

export default Notes;
