import React from "react"
import moment from "moment"

const Form = ({inputText,setInputText,notes,setNotes}) => {
    const inputTextHandler = (e) =>{
        setInputText(e.target.value);
    };
    const addNotesHandler = (e) =>{
        e.preventDefault();
        setNotes([
            ...notes, {
                text:inputText,
                dateCreated: moment().format('MMMM Do YYYY, h:mm a'),  
            }
        ]);
        setInputText(" ");
    }
    return(
        <form>
        <div className="form-container">
            <textarea value={inputText} onChange={inputTextHandler}className="todo-input"/>
            <button onClick={addNotesHandler}type="submit" className="todo-button">Add to List</button>
        </div>
        </form>
    )
}

export default Form;
