import React,{useState} from 'react'
// import EdiText from 'react-editext'

import Modal from 'react-modal'


Modal.setAppElement('#root')

const Notes = ({setInputTitle,setInputText,notes, setNotes,title,text, date}) => {
    const [modalIsOpen, setModalIsOpen] = useState(false)

    const removeHandler = (e) => {
        e.preventDefault();
        if (window.confirm("Are you sure you want to delete your note?")) {
            setNotes(notes.filter((notes => notes.date !== date)));
        }
    };

    const storeTitle = (e) => {
        setInputTitle(e.target.value);
        localStorage.setItem('title')
        setInputTitle('')
    };

    const storeText = (e) => {
        setInputText(e.target.value);
        localStorage.setItem('text')
        setInputText('')

    };

    return(
       <div className="notes">
           <li className="note-item">
                <div className="date">{date}</div>
                <button onClick={removeHandler}className="remove-btn">DELETE</button> 
                <button onClick={()=> setModalIsOpen(true)}className="modal-btn">OPEN</button>
                <div>{title}</div>
                <div>{text}</div>
            </li>  
           <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)}>
           <li className="note-item">
                <div className="date"value>{date}</div>
                <div contentEditable="false" onChange={storeTitle}>{title}</div>
                <div contentEditable="true" onChange={storeText}>{text}</div>
                <button onClick={()=> setModalIsOpen(false)}
                className="close-btn">Close</button> 
            </li>
            </Modal> 
       </div>     
    );
}

export default Notes;
