import React,{useState} from 'react'
import Modal from 'react-modal'
import EdiText from 'react-editext';

Modal.setAppElement('#root')

const Notes = ({  notes, setNotes, title, text, date}) =>{
    const [modalIsOpen, setModalIsOpen] = useState(false)
    const [editing, setEditing] = useState(false);

    const removeHandler = (e, id) => {
        e.preventDefault();
        if (window.confirm("Are you sure you want to delete your note?")) {
            setNotes(notes.filter(notes => notes.date !== date));
        }
    };

    const handleSave = (text,title) => {
        setNotes(text,title);
      };

    return(
       <div className="notes">
           <li className="note-item">
                <div className="date">{date}</div>
                <button onClick={removeHandler}className="remove-btn">DELETE</button> 
                <button onClick={()=> setModalIsOpen(true)}className="modal-btn">OPEN</button>
                <div>{title}</div>
                <div className="text">{text}</div>
            </li>  
           <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)} inEditMode>
           <li className="note-item">
                <div className="date">{date}</div>
                <button onClick={() => setEditing((v) => !v)}>Toggle</button>
            <EdiText
                value={text}
                value={title}
                type="text"
                onSave={handleSave}
                editing={editing}               
            />
                <button onClick={()=> setModalIsOpen(false)}
                className="close-btn">Close</button>
            </li>
            </Modal> 
       </div>     
    );
}

export default Notes;
